parameters.functionCode:
// Generate blog topic based on season, trends, and Denver market
const currentDate = new Date();
const month = currentDate.getMonth() + 1;
const season = getSeason(month);

// Manual trigger data or auto-generated
const manualData = items[0].json || {};

// Denver-specific seasonal topics
const seasonalTopics = {
  winter: [
    'Winter Roof Maintenance Tips for Denver Homes',
    'How to Prevent Ice Dams in Colorado',
    'Snow Load Considerations for Denver Roofs',
    'Winter Storm Damage: When to Call for Emergency Roof Repair',
    'Preparing Your Roof for Denver\'s Heavy Snow Season'
  ],
  spring: [
    'Spring Roof Inspection Checklist for Colorado Homeowners',
    'Hail Damage Assessment After Denver Spring Storms',
    'Spring Cleaning: Don\'t Forget Your Gutters and Roof',
    'Why Spring is the Best Time for Roof Replacement in Denver',
    'Post-Winter Roof Repairs: What Denver Homeowners Need to Know'
  ],
  summer: [
    'Beat the Heat: Cool Roofing Options for Denver Homes',
    'Summer Roofing Projects: Planning Your Denver Home Upgrade',
    'UV Protection: How Quality Shingles Protect Your Denver Home',
    'Ventilation Solutions for Hot Denver Summers',
    'Energy-Efficient Roofing Materials for Colorado Climate'
  ],
  fall: [
    'Fall Roof Preparation: Getting Ready for Denver Winter',
    'Gutter Cleaning and Maintenance Before Colorado Snow',
    'Why Fall is Prime Time for Roof Replacement in Denver',
    'Leaf Management: Protecting Your Roof and Gutters',
    'Pre-Winter Roof Inspection: A Denver Homeowner\'s Guide'
  ]
};

// Select topic
let selectedTopic;
if (manualData.topic) {
  selectedTopic = manualData.topic;
} else {
  const topicsForSeason = seasonalTopics[season] || seasonalTopics.spring;
  selectedTopic = topicsForSeason[Math.floor(Math.random() * topicsForSeason.length)];
}

// Generate content parameters
const contentParams = {
  topic: selectedTopic,
  target_audience: manualData.target_audience || 'Denver homeowners',
  content_type: manualData.content_type || 'educational',
  seo_keywords: manualData.seo_keywords || generateSEOKeywords(selectedTopic),
  publish_immediately: manualData.publish_immediately || false,
  word_count: 1200,
  include_cta: true,
  denver_focused: true,
  requested_at: currentDate.toISOString(),
  season: season,
  month: month
};

function getSeason(month) {
  if (month >= 12 || month <= 2) return 'winter';
  if (month >= 3 && month <= 5) return 'spring';
  if (month >= 6 && month <= 8) return 'summer';
  return 'fall';
}

function generateSEOKeywords(topic) {
  const baseKeywords = ['denver roofing', 'colorado roofing', 'roof repair denver'];
  const topicKeywords = topic.toLowerCase()
    .replace(/[^a-z0-9\s]/g, '')
    .split(' ')
    .filter(word => word.length > 3)
    .slice(0, 3);

  return [...baseKeywords, ...topicKeywords];
}

return [{ json: contentParams }];

// Extract and format the blog content
const aiContent = items[0].json.choices[0].message.content;
const originalParams = items[0].json;

// Parse the content to extract different sections
const lines = aiContent.split('\n').filter(line => line.trim());
let title = '';
let metaDescription = '';
let content = aiContent;

// Extract title (first H1 or line starting with #)
const titleMatch = aiContent.match(/^#\s*(.+)$/m);
if (titleMatch) {
  title = titleMatch[1].trim();
}

// Extract meta description if present
const metaMatch = aiContent.match(/Meta [Dd]escription:?\s*(.+)$/m);
if (metaMatch) {
  metaDescription = metaMatch[1].trim();
  // Remove meta description from content
  content = content.replace(/Meta [Dd]escription:?\s*.+$/m, '').trim();
}

// Generate slug
const slug = title.toLowerCase()
  .replace(/[^a-z0-9\s-]/g, '')
  .replace(/\s+/g, '-')
  .replace(/-+/g, '-')
  .replace(/^-|-$/g, '');

// Ensure meta description
if (!metaDescription) {
  metaDescription = `${title}. Expert roofing advice from Denver's trusted contractors. Free estimates available.`;
  if (metaDescription.length > 150) {
    metaDescription = metaDescription.substring(0, 147) + '...';
  }
}

// Structure the final blog post
const blogPost = {
  title: title || originalParams.topic,
  slug: slug,
  content: content,
  meta_description: metaDescription,
  keywords: originalParams.seo_keywords,
  author: 'Alpine Peak Roofing Team',
  status: originalParams.publish_immediately ? 'published' : 'draft',
  category: 'Roofing Tips',
  tags: [
    'Denver Roofing',
    'Colorado',
    originalParams.season,
    originalParams.content_type
  ],
  featured_image_alt: `${title} - Alpine Peak Roofing Denver`,
  schema_data: {
    '@type': 'BlogPosting',
    headline: title,
    description: metaDescription,
    author: {
      '@type': 'Organization',
      name: 'Alpine Peak Roofing'
    },
    publisher: {
      '@type': 'Organization',
      name: 'Alpine Peak Roofing'
    }
  },
  created_at: new Date().toISOString(),
  word_count: content.split(' ').length,
  readability_score: calculateReadabilityScore(content)
};

function calculateReadabilityScore(text) {
  // Simple readability estimation
  const words = text.split(' ').length;
  const sentences = text.split(/[.!?]+/).length;
  const avgWordsPerSentence = words / sentences;

  if (avgWordsPerSentence <= 15) return 'Easy';
  if (avgWordsPerSentence <= 20) return 'Medium';
  return 'Hard';
}

return [{ json: blogPost }];

